INSERT INTO `cars` (`type`, `model`, `image_url`, `price`, `color`, `amount`, `engine`, `horse_power`, `year_of_release`, `summary`)
VALUES 
('BMW', 'X1 sDrive18i', 'bmw-x1.png', 49394, 'bruin', 10, 'benzine', '162', 2009, 'De X1 heeft een soepele, atletische wegligging, een krachtige turbomotor met pittige acceleratie en verrassend laag brandstofverbruik voor zijn klasse. Automatische transmissie met handmatige functies geeft u het beste van twee werelden.'),
('BMW', 'X2 xDrive20i', 'bmw-x2.png', 59063.30, 'wit', 10, 'diesel', '178', 2018, 'De X2 is een van de meest betrouwbare gezins-SUVs die er is, met een indrukwekkende score van 97,7% in onze What Car? Betrouwbaarheidsonderzoek.'),
('BMW', 'X3 xDrive30e', 'bmw-x3.png', 67205.80, 'wit', 10, 'elektrisch', '292', 2021, 'De 2021 BMW X3 plug-in hybride wordt geleverd in slechts één uitvoering, genaamd xDrive30e. Hij wordt aangedreven door een viercilinder turbomotor gekoppeld aan een plug-in hybride systeem. Het totale vermogen bedraagt ​​288 pk en 310 lb-ft koppel. Het vermogen wordt via een automatische achtversnellingsbak naar alle vier de wielen geleid.'),
('BMW', 'X4 xDrive20i', 'bmw-x4.png', 74389.80, 'wit', 10, 'benzine', '184', 2015, 'BMW X4 is een SUV met 5 zitplaatsen. Hij is verkrijgbaar in 4 varianten, motoropties van 1998 tot 2993 cc en 1 transmissieoptie: automaat (TC). Andere belangrijke specificaties van de X4 zijn een bodemvrijheid van 204 mm, een leeggewicht van 1795 kg en een bagageruimte van 525 liter.'),
('BMW', 'X5 xDrive50e', 'bmw-x5.png', 100857.40, 'groen', 10, 'elektrisch', '489', 2023, 'De X5 xDrive45e biedt een geprojecteerd elektrisch bereik van 50 kilometer, meer dan middelgrote concurrenten, maar het door de EPA geschatte brandstofverbruik in hybride modus is verschrikkelijk met 20 mpg gecombineerd. Dat is beduidend slechter dan sommige concurrenten en slechter dan de gewone zescilinder X5.'),
('BMW', 'X6 M60i xDrive', 'bmw-x6.png', 175009.60, 'groen', 10, 'benzine', '530', 2023, 'Elke X6 is standaard uitgerust met xDrive, het intelligente vierwielaandrijvingssysteem van BMW, dat automatisch het vermogen over de wielen verdeelt voor optimale tractie en controle.'),
('BMW', 'X7 xDrive40i', 'bmw-x7.png', 140280.30, 'wit', 10, 'benzine', '381', 2019, 'De BMW X7 wordt standaard geleverd met Active Driving Assistant, waaronder veiligheidssystemen als Active Blind Spot Detection, Frontal Collision Warning met City Collision Mitigation en Lane Departure Warning. Het optionele Driving Assistance Professional-pakket is ook beschikbaar, inclusief Frontal Collision Warning met City Collision Mitigation en Extended Traffic Jam Assistant.'),
('BMW', 'Z4 sDrive20i', 'bmw-z4.png', 63737, 'paars', 10, 'benzine', '197', 2023, 'BMW Z4 sDrive 20i is de benzinevariant in de BMW Z4-serie. Het levert een gecertificeerde kilometerstand van 14,3 kmpl op. Deze sDrive 20i-variant is het BMW Z4-basismodel en wordt geleverd met een motor die 194 pk levert bij 4500 tpm en 320 Nm bij 1450 tpm aan respectievelijk maximaal vermogen en maximaal koppel.'),
('BMW', 'i8', 'bmw-i8.jpg', 147500, 'zwart', 10, 'elektrisch', '231', 2023, 'De BMW i8 was het eerste productiemodel van BMW dat werd aangedreven door een driecilinder benzinemotor en het resulterende specifieke vermogen van 115 kW (154 pk) per liter cilinderinhoud was vergelijkbaar met high-performance sportwagenmotoren en was het hoogste van alle elke motor geproduceerd door de BMW Group.'),
('BMW', ' XM', 'bmw-xm.png', 181274.30, 'blauw', 10, 'elektrisch', '653', 2023, 'De BMW XM is een plug-in hybride elektrische full-size luxe cross-over SUV vervaardigd door BMW onder de BMW M-dochteronderneming. Het is de tweede auto die volledig door BMW M is ontwikkeld, na de BMW M1 in 1978. Een krachtigere versie, genaamd "Label Red", werd ook aangekondigd naast de onthulling van de XM op 27 september 2022.'),
('BMW', '1 Series', 'bmw-1-serie.png', 37142.40, 'blauw', 10, 'benzine', '109', 2020, 'Ontdek de BMW 1 Serie. De compacte BMW onderscheidt zich met een onmiskenbaar design, sportiviteit en baanbrekende technologieën. Het is in ieder opzicht een echte BMW en staat dus garant voor een dynamische rijbeleving.'),
('BMW', '218i Coupé', 'bmw-2-serie.png', 49637.30, 'wit', 10, 'benzine', '156', 2021, 'het is de minst krachtige BMW-auto in het gamma met slechts drie cilinders, 140 pk en 220 Nm, maar geloof me als ik zeg dat hij allesbehalve traag is. Deze auto is zelfs sneller en rijdt veel beter dan veel andere autos op onze wegen'),
('BMW', '320d Sedan', 'bmw-3-serie.png', 52158.80, 'wit', 10, 'diesel', '122', 2018, 'U profiteert van een hoogwaardige standaarduitrusting en de typische BMW rijdynamiek.'),
('BMW', '420i Coupé', 'bmw-4-serie.png', 57920.30, 'wit', 10, 'benzine', '184', 2019, 'De prestaties van de 4-serie zijn sterk, met de 181 pk sterke 420i-benzineversie die een tijd van 0-100 km/u in 7,5 seconden aflegt. De 430i (niet meer beschikbaar op de prijslijst) ziet een vermogenstoename tot 254 pk en een verbeterde sprint naar 100 km/u in slechts 5,8 seconden.'),
('BMW', '518d Sedan', 'bmw-5-serie.png', 65122.20, 'wit', 10, 'diesel', '150', 2020, 'De BMW 5 Serie bouwt verder op zijn efficiëntievoorsprong in de executive klasse. Nieuwe generatie viercilinder dieselmotoren met BMW TwinPower Turbo-technologie verhogen het rijplezier van de wereldwijde bestseller en zorgen voor een verdere verlaging van het brandstofverbruik en de emissies – De nieuwe BMW 520d overtreft zijn segmentrivalen in de efficiëntierace: motorvermogen stijgt tot 140 kW/190 pk, het gemiddelde brandstofverbruik daalt tot slechts 4,1 l/100 km en de CO2-uitstoot tot 109 g/km.'),
('BMW', 'M3', 'bmw-m3.jpg', 75295, 'grijs', 10, 'benzine', '240', 1995, 'De eerste M3 werd gemaakt als homologatiespecial op basis van de 3-serie coupé, zodat BMW hem kon gebruiken om deel te nemen aan verschillende toerwagenkampioenschappen over de hele wereld. Er moesten er slechts 5.000 worden geproduceerd om aan de Groep A-voorschriften te voldoen, maar uiteindelijk rolden er bijna 18.000 de fabriek uit.');
INSERT INTO `cars` (`type`, `model`, `image_url`, `price`, `color`, `amount`, `engine`, `horse_power`, `year_of_release`, `summary`)
VALUES
('audi', 'Q8 e-tron', 'Q8 e-tron.png', 71385, 'grijs', 4, 'elektrisch', '408', 2023, 'De Q8 e-tron is net zo executive als je hier zou verwachten. Zelfs de basisafwerking van de Q8 e-tron is zo goed uitgerust dat je je nauwelijks kunt voorstellen hoe Audi er nog meer spullen in zou kunnen rammen en de prijs zou kunnen opdrijven (spoiler: het lukt). Je krijgt 20-inch wielen, luchtvering, LED-koplampen, een elektrisch te openen kofferbak, elektrisch verstelbare verwarmde voorstoelen, alle touchscreens die je maar kunt wensen en 360 graden parkeersensoren.'),
('audi', 'A1 Sportback', 'a1.png', 30000, 'zwart', 10, 'diesel', '149', 2008, 'De tweede generatie van de A1 Sportback heeft een volledig nieuw en karakteristiek design, daarbij geeft de A1 een knipoog naar de eerste Audi quattro. De A1 Sportback is niet alleen de sportiefste in zijn klasse, maar zet ook de maatstaf voor de rest in de klasse van compacte autos absolute topkwaliteit.'),
('audi', 'AG', 'audi-AG.png', 100000, 'grijs', '1', 'diesel', '50', 2010, 'Audi AG kan terugkijken op een gevarieerde en bewogen geschiedenis, waarvan de traditie teruggaat tot de 19e eeuw. Tijdens een eerste concentratiegolf in de auto-industrie fuseerde Audi in 1932 met Horch, DKW en Wanderer tot Auto Union AG. De vier met elkaar verweven ringen van het Audi-logo doen nog steeds denken aan de stichtende merken. Vandaag is Audi een van de belangrijkste Duitse premiummerken naast Mercedes-Benz en BMW. Werner Oswald volgt in deze bundel de geschiedenis van deze opkomst en de modellen waarmee Auto Union en zijn erfgenamen deze prestatie hebben geleverd.'),
('audi', 'A3 Sportback', 'audi-A3-sportback.png', 37500, 'blauw', 10, 'benzine', '115', 2010, 'De 4e generatie van de Audi A3 is niet vernieuwend en biedt geen unieke voorzieningen. Toch weet de auto zich te onderscheiden met verfijning in de vormgeving en de rijeigenschappen.'),
('audi', 'A4 Avant', 'audi-A4.png', 37140, 'grijs', 15, 'benzine', '265', 2014, 'Audi presenteert de 5e generatie van de A4. De A4 rijdt prima, maar niet beter dan gemiddeld. De techniek is modern, maar niet vooruitstrevend. Het strakke uiterlijk is en blijft het sterkste punt.'),
('audi', 'A5 Sportback', 'a5-sportback.png', 43285, 'grijs', 14, 'benzine', '150', 2018, 'De Audi A5 Coupe ziet er niet alleen goed uit, maar rijdt ook zo. Enige vorm van "voorsprong door techniek" is ver te zoeken, maar door bestaande techniek te verfijnen is de A5 sportief en comfortabel'),
('audi', 'A6', 'audi-a6.png', 61245, 'grijs', 10, 'diesel', '204', 2018, 'De nieuwe Audi A6 (2018) is niet wezenlijk anders dan de vorige, maar doet alles beter. Hij biedt meer comfort en tegelijkertijd meer dynamiek. De semi-zelfrijdende functies maken de auto veiliger.'),
('audi', 'A7', 'audi-a7.png', 89315, 'grijs', 10, 'benzine', '340', 2018, 'De Audi A7 Sportback combineert de geavanceerde elektronica van het topmodel met de dynamiek van de andere modellen. Het lijnenspel combineert een 4-deurs sedan met een coupe.'),
('audi', 'A8', 'audi-a8.png', 105695, 'grijs', 10, 'benzine', '340', 2017, 'De Audi A8 is klaar voor de toekomst met hybride aandrijving en verregaande zelfrijdende functies. Zonder die opties scoort de A8 vooral met het comfort en de rijeigenschappen.'),
('audi', 'Q2', 'audi-q2.png', 31145, 'geel', 5, 'benzine', '116', 2016, 'De Q2 is de compacte, betaalbare SUV van Audi. De Q2 overtuigt met uitstekende rijeigenschappen en een geweldige 1.0 TFSI motor. Helaas zijn de 1.6 diesel en de materiaalkeuze minder.'),
('audi', 'Q3', 'audi-q3.png', 44220, 'blauw', 10, 'benzine', '150', 2018, 'De Audi Q3 is de droomauto voor perfectionisten. Alles is mooi, alles werkt perfect. Dat is goed, maar het maakt deze SUV weinig avontuurlijk en zelfs een tikkeltje saai.'),
('audi', 'Q4', 'audi-q4.png', 52105, 'zwart', 23, 'elektrisch', '170', 2021, 'De Q4 is een elektrisch aangedreven SUV volgens Audi: vertrouwd en toch modern. Vlot en toch dynamisch. Prima rijeigenschappen, groot bereik, moderne uitrusting. Een aanrader!'),
('audi', 'Q5', 'audi-q5.png', 59700, 'grijs', 8, 'benzine', '245', 2017, 'De Audi Q5 onderscheidt zich met verfijning en sublieme rijeigenschappen. De Q5 is geen terreinauto, maar een extra stoere zakenauto. Kostbare opties noodzakelijk voor een complete auto.'),
('audi', 'Q7', 'audi-q7.png', 83090, 'grijs', 8, 'benzine', '340', 2015, 'De Audi Q7 voor modeljaar 2020 biedt de laatste techniek die Audi in huis heeft. De Q7 combineert dat met maximaal comfort, volop ruimte en (beperkte) terreinwaardigheid.'),
('audi', 'TT', 'audi-tt.png', 44340, 'blauw', 15, 'benzine', '180', 2014, 'De nieuwe TT is het paradepaardje van Audi. Zowel de vormgeving als de ergonomie zijn echt innovatief. Daarbij is de nieuwe TT comfortabeler en tegelijk sportiever dan voorheen.'),
('audi', 'R8', 'audi-r8.png', 238940, 'geel', 5, 'benzine', '540', 2015, 'De Audi R8 is een supersportwagen die zich leent voor dagelijks gebruik en voor snelle rondetijden op het circuit. Vanaf modeljaar 2019 nog sneller en nog makkelijker te rijden.');
INSERT INTO `cars` (`type`, `model`, `image_url`, `price`, `color`, `amount`, `engine`, `horse_power`, `year_of_release`, `summary`)
VALUES
('mercedes', 'A-klasse', 'a-classe.png', 31466, 'grijs', 10, 'benzine', '109', 2018, 'De Mercedes-Benz A250e biedt dankzij plug-in hybride aandrijving het beste van twee werelden. De A250e is snel en dynamisch indien gevraagd, maar comfortabel en zuinig bij dagelijks gebruik.'),
('mercedes', 'CLA', 'cla.png', 37466, 'zwart', 10, 'diesel', '116', 2019, 'De Mercedes-Benz CLA wil een jonge doelgroep aanspreken en doet dat overtuigend met een extravagant uiterlijk, sportieve rijeigenschappen (CLA 220) en een moderne, vooruitstrevende uitrusting.'),
('mercedes', 'CLA Shooting Brake', 'CLA Shooting Brake.png', 38918, 'zwart', 5, 'benzine', '136', 2019, 'De Mercedes-Benz CLA Shooting Brake is een stationcar met de lijnen van een coupe. Rijdt scherp en sportief. Ook als CLA 200 comfortabel en vlot.'),
('mercedes', 'C-Klasse', 'c-klasse.png', 48280, 'grijs', 10, 'benzine', '170', 2021, 'Met de 5e generatie van de C-Klasse geeft Mercedes-Benz een moderne interpretatie aan de traditionele sedan. Mild-hybrid maakt de C180 zuinig. MBUX 2.0 is krachtig en toch eenvoudig te bedienen.'),
('mercedes', 'C-Klasse Estate', 'C-Klasse Estate.png', 54649, 'grijs', 10, 'benzine', '170', 2021, 'De Mercedes-Benz C-Klasse Estate combineert een moderne uitrusting met ruimte en comfort. De motoren zijn conventioneel. De C200 leent zich zowel voor comfort als dynamiek.'),
('mercedes', 'E-Klasse Estate', 'E-Klasse Estate.png', 63536, 'wit', 10, 'benzine', '184', 2016, 'De nieuwe Mercedes-Benz E-Klasse Estate onderscheidt zich met luxe, veiligheid en ruimte dankzij een intelligent ontwerp. De E400 biedt nog meer comfort omdat met groot gemak wordt gepresteerd.'),
('mercedes', 'E-Klasse', 'e-klasse.png', 59735, 'grijs', 10, 'benzine', '184', 2016, 'De Mercedes-Benz E300e biedt dankzij hybride aandrijving meer comfort, betere prestaties dan een gewone E-Klasse. Het verbruik is desondanks laag tot nul. Alleen maar voordelen!'),
('mercedes', 'E-Klasse Coupe', 'E-Klasse Coupe.png', 58812, 'grijs', 10, 'benzine', '197', 2017, 'Mercedes-Benz heeft goed werk geleverd met de E-Klasse Coupe, want de auto rijdt en voelt echt anders dan de sedan. Uiteraard beschikt de coupe wel dezelfde vooruitstrevende techniek.'),
('mercedes', 'S-Klasse', 'S-Klasse.png', 130682, 'zwart', 10, 'benzine', '435', 2020, 'De 7e generatie van de Mercedes S-Klasse legt de nadruk op de uitrusting en de rijeigenschappen, nu elektrische autos al ultiem comfort bieden. Veel vernieuwing en pure verwennerij voor de passagiers'),
('mercedes', 'SL', 'SL.png', 161696, 'rood', 10, 'benzine', '381', 2022, 'Met de SL zet Mercedes een oude traditie voort in de nieuwe tijd. Op het gebied van vormgeving, ergonomie, veiligheid en luxe is dat gelukt. De V8 is niet van deze tijd en maakt de SL onnodig kostbaar'),
('mercedes', 'GT', 'GT.png', 190867, 'zwart', 1, 'benzine', '476', 2014, 'De Mercedes-AMG GT combineert het comfort en de beschaafde lijnen met een Gran Turismo, met de prestaties en de sensatie van een supersportwagen.'),
('mercedes', 'B-Klasse', 'B-Klasse.png', 48087, 'wit', 15, 'benzine', '136', 2019, 'De Mercedes-Benz B-Klasse combineert ruimte met luxe. De rijeigenschappen zijn voldoende. De vele comfort-functies maken de B-Klasse tot een bijzondere auto.'),
('mercedes', 'G-Klasse', 'G-Klasse.png', 188220, 'wit', 5, 'diesel', '286', 2018, 'De Mercedes-AMG G63 is een auto van extremen. De G-Klasse is sterk in het terrein en razendsnel op de autobahn. De uitrusting is vergelijkbaar met die van een S-Klasse.'),
('mercedes', 'GLE Coupe', 'GLE Coupe.png', 125623, 'grijs', 5, 'diesel', '330', 2020, "De Mercedes-Benz GLE Coupe onderscheidt zich van andere luxe SUV's met zowel een meer sportief karakter als een bijzonder lijnenspel. Dit doet geen afbreuk aan de ruimte of terreinwaardigheid."),
('mercedes', 'GLS', 'GLS.png', 129228, 'grijs', 5, 'diesel', '330', 2020, 'De GLS is niet alleen de grootste SUV van Mercedes, maar ook van Nederland. De binnenruimte is enorm en wordt gecombineerd met de luxe van een topklasse reisauto. Kostbaar in aanschaf en gebruik.'),
('mercedes', 'GLA', 'GLA.png', 49267, 'grijs', 5, 'benzine', '163', 2020, 'De Mercedes-Benz GLA 250 e is een plug-in hybride die het beste biedt van vele werelden. Deze SUV is ruim en praktisch, vlot en zuinig, stil en comfortabel, sportief en comfortabel.')
INSERT INTO `cars` (`type`, `model`, `image_url`, `price`, `color`, `amount`, `engine`, `horse_power`, `year_of_release`, `summary`)
VALUES
('volkswagen', 'Polo', 'Polo.png', 19990, 'wit', 15, 'benzine', '80', 2017, 'De Volkswagen Polo GTI is functioneel en comfortabel wanneer het moet, maar razendsnel en spectaculair wanneer het mag. De GTI is snel, maar voelt nog sneller dan hij is dankzij het brute karakter.'),
('volkswagen', 'Taigo', 'Taigo.png', 28590, 'groen', 12, 'benzine', '95', 2021, 'De Volkswagen Taigo is een compacte SUV met de daklijn van een coupe. Dat gaat nauwelijks ten koste van de binnenruimte, maar de rijeigenschappen verschillen slechts op details van andere Volkswagens.'),
('volkswagen', 'T-Cross', 'T-Cross.png', 29400, 'zwart', 14, 'benzine', '95', 2019, 'De T-Cross is de compacte SUV van Volkswagen. Veel aandacht voor infotainment, veiligheid en uiterlijk. Het mechaniek is behoudend en grotendeels gelijk aan dat van de VW Polo'),
('volkswagen', 'Golf', 'Golf.png', 34990, 'geel', 10, 'benzine', ' 110', 2020, 'De Volkswagen Golf GTE biedt een slaagde combinatie van elektrische en conventionele aandrijving. De GTE is vlot en sportief, maar heeft het zorgeloze bereik van een auto met benzinemotor.'),
('volkswagen', 'Golf Variant', 'Golf Variant.png', 28690, 'geel', 5, 'benzine', '110', 2021, 'De Variant biedt dankzij een verlengde wielbasis en extra overhang meer ruimte dan de standaard Golf. De uitrusting is modern en de eTSI motor relatief vlot en zuinig. Moderne uitrusting.'),
('volkswagen', 'ID.3', 'ID.3.png', 33990, 'grijs', 8, 'elektrisch', '150', 2020, "De Volkswagen ID.3 is de auto die elektrisch rijden gewoon maakt. De ID.3 biedt alle voordelen die andere elektrische auto's bieden, maar maakt ze toegankelijker dankzij de prijs en verpakking."),
('volkswagen', 'Passat', 'Passat.png', 39575, 'grijs', 10, 'elektrisch', '218', 2014, 'De Volkswagen Passat zet wederom de standaard. De Passat is verfijnd, snel en luxueus, maar straalt elegantie en eenvoud uit; precies wat anno 2014 van een zakenauto wordt gevraagd.'),
('volkswagen', 'Passat Variant', 'Passat Variant.png', 41270, 'grijs', 10, 'elektrisch', '218', 2014, 'Niet vernieuwend, niet bijzonder en niet opvallend. Tegelijkertijd ruim, praktisch, comfortabel en modern waardoor de nieuwe Volkswagen Passat perfect aansluit bij de wensen van de zakelijke rijder'),
('volkswagen', 'Arteon', 'Arteon.png', 48950, 'grijs', 10, 'benzine', '190', 2017, 'De Volkswagen Arteon is de eerste vierdeurs coupé van een alledaags merk. Gezien de prijs is de 1.5 TSI een prima aanbod en rijdt de auto zo goed als hij er uitziet.'),
('volkswagen', 'Arteon Shooting Brake', 'Arteon Shooting Brake.png', 49490, 'blauw', 10, 'benzine', '190', 2020, 'De Volkswagen Arteon start een tweede leven als Shooting Brake. Een exclusief interieur, een rijke uitrusting en plug-inhybride aandrijving maken de Arteon klaar voor de toekomst.'),
('volkswagen', 'T-Roc', 'T-Roc.png', 32790, 'wit', 10, 'benzine', '110', 2017, 'De Volkswagen T-Roc R is een sportieve versie van een alledaagse gezinsauto. De R is sensationeel snel, maar kostbaar in aanschaf en gebruik'),
('volkswagen', 'T-Roc Cabrio', 'T-Roc Cabrio.png', 36600, 'blauw', 10, 'benzine', '115', 2020, 'De Volkswagen T-Roc Cabriolet heeft geen uitgesproken rijeigenschappen, maar wel een uitgesproken karakter. Dit is een echte zonaanbidder die uitnodigt om zo veel mogelijk open te rijden.'),
('volkswagen', 'Tiguan', 'Tiguan.png', 36390, 'wit', 10, 'benzine', '130', 2016, 'De Volkswagen Tiguan vanaf modeljaar 2021 is bedoeld voor wie alles graag bij het vertrouwde laat. De uitrusting is gemoderniseerd en verder is alles bij het oude gebleven: op alle punten goed.'),
('volkswagen', 'Tiguan Allspace', 'Tiguan Allspace.png', 42290, 'zwart', 10, 'benzine', '150', 2017, 'De Volkswagen Tiguan AllSpace combineert het stoere uiterlijk van een SUV met de ruimte van een 5+2 persoons MPV. Moderne techniek, prima rijeigenschappen en zelfs vaardig in het terrein.'),
('volkswagen', 'Multivan', 'Multivan.png', 54500, 'oranje', 4, 'benzine', '218', 2022, 'De Volkswagen Multivan is een zeer grote gezinsauto die dankzij hybride-aandrijving hetzelfde kost als een vergelijkbare SUV. Dankzij personenauto-techniek biedt de Multivan bovengemiddeld comfort.'),
('volkswagen', 'ID.Buzz', 'ID.Buzz.png', 57132, 'geel', 3, 'elektrisch', '204', 2022, "De Volkswagen ID.Buzz combineert aantrekkelijke vormgeving met elektrische aandrijving. Het resultaat is een auto die ruimer, comfortabeler, stabieler en schoner is dan traditionele gezinsauto's.")
INSERT INTO `cars` (`type`, `model`, `image_url`, `price`, `color`, `amount`, `engine`, `horse_power`, `year_of_release`, `summary`)
VALUES
('ford-pickup', 'Ford F-150', 'f-150.png', 55690, 'grijs', 5, 'benzine', '290', 2014, "De Ford F-150 is een populaire pickup truck van het Amerikaanse automerk Ford. Het voertuig is voor het eerst geïntroduceerd in 1948 en is sindsdien uitgegroeid tot een van de best verkopende auto's ter wereld."),
('ford-pickup', 'Ford Ranger', 'ford ranger.png', 35000, 'grijs', 5, 'benzine', '270', 2011, 'De Ford Ranger is een midsize pickup truck die wordt geproduceerd door het Amerikaanse automerk Ford. Het voertuig werd voor het eerst geïntroduceerd in 1983 en was tot 2011 beschikbaar in de Verenigde Staten, waarna de productie werd stopgezet. De Ranger werd echter in 2019 opnieuw geïntroduceerd in de VS en is momenteel verkrijgbaar in verschillende landen over de hele wereld. '),
('ford-pickup', 'Ford F-250 Super Duty', 'f-250.png', 55990, 'wit', 5, 'benzine', '475', 2008, 'De Ford F-250 is een heavy-duty pickup truck die wordt geproduceerd door het Amerikaanse automerk Ford. Het voertuig maakt deel uit van de Ford F-serie, die sinds 1948 op de markt is en bestaat uit verschillende modellen van lichte tot heavy-duty trucks.'),
('ford-pickup', 'Ford F-350 Super Duty', 'f-350.png', 69420, 'wit', 5, 'benzine', '385', 2012, 'De Ford F-350 is een heavy-duty pick-up truck die geproduceerd wordt door het Amerikaanse autobedrijf Ford. Het is een van de modellen in de Ford F-serie, die sinds 1948 op de markt is en bestaat uit verschillende modellen van lichte tot heavy-duty trucks.'),
