CREATE database wheelsOfFortune;

USE `wheelsOfFortune`;

CREATE TABLE `users` (
    id MEDIUMINT NOT NULL AUTO_INCREMENT PRIMARY KEY,
    username VARCHAR(100) NOT NULL,
    password varchar(100) NOT NULL
);
Create TABLE `cars` (
    id MEDIUMINT NOT NULL AUTO_INCREMENT PRIMARY KEY,
    type ENUM('BMW', 'mercedes', 'audi', 'volkswagen', 'ford-pickup', 'dodge', 'jeep', 'lexus', 'landrover', 'mini'),
    model VARCHAR(100) NOT NULL,
    image_url VARCHAR(100) NOT NULL,
    price INT NOT NULL,
    color ENUM('zwart', 'wit', 'rood', 'blauw', 'groen', 'geel', 'paars', 'oranje', 'grijs', 'bruin', 'zilver', 'goud', 'roze', 'transparant', 'multicolor'),
    amount INT NOT NULL,
    engine ENUM('diesel', 'benzine', 'elektrisch') NOT NULL,
    horse_power VARCHAR(100) NOT NULL,
    year_of_release int NOT NULL,
    summary TEXT NOT NULL
);

CREATE TABLE `owners` (
    naam VARCHAR(255),
    age INT,
    summary TEXT,
    class VARCHAR(255),
    instagram VARCHAR(255),
    snapchat VARCHAR(255)
);

